import React from 'react';
import './index.css';
import openSocket from 'socket.io-client';

// npm i socket.io-client

function Square(props) {

    return (
        <button 
          className="square" 
          onClick={props.onClick}
        >
            {props.value}
        </button>
    );
}

class Board extends React.Component {

	renderSquare(i) {
    	return (
    		<Square 
    			value={this.props.squares[i]}
    			onClick={() => this.props.onClick(i)}
    		/>);
	}

	render() {
		return (
			<div>
				<div className="board-row">
					{this.renderSquare(0)}
					{this.renderSquare(1)}
					{this.renderSquare(2)}
				</div>
				<div className="board-row">
					{this.renderSquare(3)}
					{this.renderSquare(4)}
					{this.renderSquare(5)}
				</div>
				<div className="board-row">
					{this.renderSquare(6)}
					{this.renderSquare(7)}
					{this.renderSquare(8)}
				</div>
			</div>
    	);
    }
}

let getParamValue = function(paramName)
{
    var url = window.location.search.substring(1); //get rid of "?" in querystring
    var qArray = url.split('&'); //get key-value pairs
    for (var i = 0; i < qArray.length; i++) 
    {
        var pArr = qArray[i].split('='); //split key and value
        if (pArr[0] === paramName) 
           console.log(pArr[1]);
            return pArr[1]; //return value
    }
}

class Game extends React.Component {
	constructor(props) {
    	super(props);
    	var url = window.location.search.substring(1);
    	var qArray = url.split('&');

    	let host = qArray[1].split('=');


    	this.state = {
      		squares: Array(9).fill(null),
    		xIsNext: true,
    		token: qArray[0].split('='),
    		socket: openSocket('localhost:6000'),
    		playmaster: qArray[1].split('='),
    		message: 'Waiting for another player...',
    		type: '-',
    	};

    	let self = this;
    	this.state.socket.on('type', type => {
      		this.setState(...self.state, {type: type})
    	});

   //  	this.state.socket.on('color', color => {
   //    		this.setState(...self.state, {color: color})
   // 		});

   //  	this.state.socket.on('turn', player => {
   //    		if (player === this.state.color) {
	  //       	this.setState(...self.state, {message: "You're up. What's your move?", yourTurn: true})
	  //     	} else {
	  //     		this.setState(...self.state, {message: player + ' is thinking...', yourTurn: false})
	  //   	}
	 	// }



  }

  handleClick(i){
		const squares = this.state.squares.slice();   //create a copy of the array

		//if the game is over or the sqare is already filled, return
		if (calculateWinner(squares) || squares[i]){
			return;
		}

		squares[i] = this.state.xIsNext ? 'X' : 'O';
		this.setState({
			squares: squares,
			xIsNext: !this.state.xIsNext,
		});
	}

	render() {
		const winner = calculateWinner(this.state.squares);
  		let status;

	  	if (winner) {
	  		status = 'Winner:' + winner;
	  	}else{
	  		status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
	  	}

	  	let test1 = this.state.token;

    	return (
			<div className="game">
			<div className="game-board">
				<Board 
					squares={this.state.squares}
					onClick={(i) => this.handleClick(i)}
				/>
			</div>
			<div className="game-info">
			  <div>{status}</div>
			  <div>{this.state.type}</div>
			  <div>{this.state.playmaster}</div>
			</div>
			</div>
    );
  }
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

export default Game;
