export function showGameStatus(status){
	let message;

	switch(status){
		case -3:
			message = 'Unauthorized connection.';
			break;
		case -2:
			message = 'Redirecting...';
			break;
		case -1:
			message = 'No connection found.';
			break;
		case 0:
			message = 'Connected!';
			break;
		case 1:
			message = 'Connected! Wait for  opponent to connect.';
			break;
		case 2:
			message = 'Ready to play!';
			break;
		case 3:
			message = 'The game is completed!';
			break;
		case 4:
			message = 'The opponent left.';
			break;
	}

	return message;
}


